contracts/lib/LibDiamond.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;
pragma experimental ABIEncoderV2;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamond Standard: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

library LibDiamond {
    bytes32 public constant DIAMOND_STORAGE_POSITION =
        keccak256("diamond.standard.diamond.storage");

    struct FacetAddressAndPosition {
        address facetAddress;
        uint16 functionSelectorPosition; // position in facetFunctionSelectors.functionSelectors array
    }

    struct FacetFunctionSelectors {
        bytes4[] functionSelectors;
        uint16 facetAddressPosition; // position of facetAddress in facetAddresses array
    }

    struct DiamondStorage {
        // maps function selector to the facet address and
        // the position of the selector in the facetFunctionSelectors.selectors array
        mapping(bytes4 => FacetAddressAndPosition) selectorToFacetAndPosition;
        // maps facet addresses to function selectors
        mapping(address => FacetFunctionSelectors) facetFunctionSelectors;
        // facet addresses
        address[] facetAddresses;
        // Used to query if a contract implements an interface.
        // Used to implement ERC-165.
        mapping(bytes4 => bool) supportedInterfaces;
        // owner of the contract
        address contractOwner;
    }

    function diamondStorage()
        internal
        pure
        returns (DiamondStorage storage ds)
    {
        bytes32 position = DIAMOND_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    function enforceIsContractOwner() internal view {
        require(
            msg.sender == diamondStorage().contractOwner,
            "LibDiamond: invalid contract owner"
        );
    }
}

contracts/facets/Bond/StakingFacet.sol.content:
// SPDX-License-Identifier: AGPL-3.0-or-later
pragma abicoder v2;
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../../lib/LibBondStorage.sol";
import "./Facet.sol";

interface IDistributor {
    function distribute() external returns (uint256);
}

interface IsNecc is IERC20 {
    function mint(address _to, uint256 _amount) external;

    function burn(address _from, uint256 _amount) external;

    function index() external view returns (uint256);

    function balanceFrom(uint256 _amount) external view returns (uint256);

    function balanceTo(uint256 _amount) external view returns (uint256);

    function migrate(address _staking, address _sOHM) external;
}

interface InNecc is IERC20 {
    function rebase(uint256 neccProfit_, uint256 epoch_)
        external
        returns (uint256);

    function circulatingSupply() external view returns (uint256);

    function balanceOf(address who) external view override returns (uint256);

    function gonsForBalance(uint256 amount) external view returns (uint256);

    function balanceForGons(uint256 gons) external view returns (uint256);

    function index() external view returns (uint256);
}

contract StakingFacet is Facet {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using SafeERC20 for InNecc;

    /**
        @notice stake Necc to enter warmup
        @param _amount uint
        @return bool
     */
    function stake(uint256 _amount, address _recipient)
        external
        returns (bool)
    {
        rebase();

        IERC20(s.Necc).safeTransferFrom(msg.sender, address(this), _amount);

        if (s.warmupPeriod == 0) {
            _send(_recipient, _amount);
        } else {
            LibBondStorage.Claim memory info = s.warmupInfo[_recipient];
            require(!info.lock, "Deposits for account are locked");

            s.warmupInfo[_recipient] = LibBondStorage.Claim({
                deposit: info.deposit.add(_amount),
                gons: info.gons.add(InNecc(s.nNecc).gonsForBalance(_amount)),
                expiry: s.epoch.number.add(s.warmupPeriod),
                lock: false
            });

            s.gonsInWarmup = s.gonsInWarmup.add(
                InNecc(s.nNecc).gonsForBalance(_amount)
            );
        }
        return true;
    }

    /**
        @notice retrieve nNecc from warmup
        @param _recipient address
     */
    function claim(address _recipient) public {
        LibBondStorage.Claim memory info = s.warmupInfo[_recipient];
        if (!info.lock) {
            require(
                _recipient == msg.sender,
                "External claims for account are locked"
            );
        }

        if (s.epoch.number >= info.expiry && info.expiry != 0) {
            delete s.warmupInfo[_recipient];
            s.gonsInWarmup = s.gonsInWarmup.sub(info.gons);

            _send(_recipient, InNecc(s.nNecc).balanceForGons(info.gons));
        }
    }

    /**
        @notice forfeit nNecc in warmup and retrieve Necc
     */
    function forfeit() external {
        LibBondStorage.Claim memory info = s.warmupInfo[msg.sender];
        delete s.warmupInfo[msg.sender];
        s.gonsInWarmup = s.gonsInWarmup.sub(info.gons);

        IERC20(s.Necc).safeTransfer(msg.sender, info.deposit);
    }

    /**
        @notice redeem nNecc for Necc
        @param _amount uint
        @param _trigger bool
     */
    function unstake(uint256 _amount, bool _trigger) external {
        if (_trigger) {
            rebase();
        }
        IERC20(s.nNecc).safeTransferFrom(msg.sender, address(this), _amount);
        IERC20(s.Necc).safeTransfer(msg.sender, _amount);
    }

    /**
     * @notice send staker their amount as sOHM or gOHM
     * @param _recipient address
     * @param _amount uint
     */
    function _send(address _recipient, uint256 _amount)
        internal
        returns (uint256)
    {
        InNecc(s.nNecc).safeTransfer(_recipient, _amount); // send as sOHM (equal unit as OHM)
        return _amount;
    }

    /**
     * @notice convert _amount nNecc into sBalance_ sNecc
     * @param _to address
     * @param _amount uint
     * @return sBalance_ uint
     */
    function wrap(address _to, uint256 _amount)
        external
        returns (uint256 sBalance_)
    {
        InNecc(s.nNecc).safeTransferFrom(msg.sender, address(this), _amount);

        sBalance_ = IsNecc(s.sNecc).balanceTo(_amount);
        IsNecc(s.sNecc).mint(_to, sBalance_);
    }

    /**
     * @notice convert _amount s.sNecc into sBalance_ s.nNecc
     * @param _to address
     * @param _amount uint
     * @return sBalance_ uint
     */
    function unwrap(address _to, uint256 _amount)
        external
        returns (uint256 sBalance_)
    {
        IsNecc(s.sNecc).burn(msg.sender, _amount);

        sBalance_ = IsNecc(s.sNecc).balanceFrom(_amount);
        InNecc(s.nNecc).safeTransfer(_to, sBalance_);
    }

    //

    /**
        @notice returns the nNecc index, which tracks rebase growth
        @return uint
     */
    function index() public view returns (uint256) {
        return InNecc(s.nNecc).index();
    }

    /**
        @notice trigger rebase if epoch over
     */
    function rebase() public {
        if (s.epoch.endTime <= uint256(block.timestamp)) {
            InNecc(s.nNecc).rebase(s.epoch.distribute, s.epoch.number);

            s.epoch.endTime = s.epoch.endTime.add(s.epoch.length);
            s.epoch.number++;

            IDistributor(address(this)).distribute();

            if (contractBalance() <= totalStaked()) {
                s.epoch.distribute = 0;
            } else {
                s.epoch.distribute = contractBalance().sub(totalStaked());
            }
        }
    }

    /**
        @notice returns contract Necc holdings, including bonuses provided
        @return uint
     */
    function contractBalance() public view returns (uint256) {
        return IERC20(s.Necc).balanceOf(address(this)).add(s.totalBonus);
    }

    /**
        @notice provide bonus to locked staking contract
        @param _amount uint
     */
    function giveLockBonus(uint256 _amount) external {
        require(msg.sender == s.locker);
        s.totalBonus = s.totalBonus.add(_amount);
        IERC20(s.nNecc).safeTransfer(s.locker, _amount);
    }

    /**
        @notice reclaim bonus from locked staking contract
        @param _amount uint
     */
    function returnLockBonus(uint256 _amount) external {
        require(msg.sender == s.locker);
        s.totalBonus = s.totalBonus.sub(_amount);
        IERC20(s.nNecc).safeTransferFrom(s.locker, address(this), _amount);
    }

    function epoch() public view returns (LibBondStorage.Epoch memory) {
        return s.epoch;
    }

    function warmupInfo(address _recipient)
        public
        view
        returns (LibBondStorage.Claim memory)
    {
        return s.warmupInfo[_recipient];
    }

    function supplyInWarmup() public view returns (uint256) {
        return InNecc(s.nNecc).balanceForGons(s.gonsInWarmup);
    }

    function totalStaked() public view returns (uint256) {
        return InNecc(s.nNecc).circulatingSupply();
    }
}

contracts/facets/Bond/DistributorFacet.s.content:
// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./Facet.sol";
import "../../lib/LibBondStorage.sol";
import "./BondDepositoryLib.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

interface ITreasury {
    function mintRewards(address _recipient, uint256 _amount) external;
}

contract DistributorFacet is Facet {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using BondDepositoryLib for LibBondStorage.Storage;

    /* ====== PUBLIC FUNCTIONS ====== */

    /**
        @notice send epoch reward to staking contract
     */
    function distribute() external returns (bool) {
        uint256 _principleLength = EnumerableSet.length(s.principles);
        bool _success = true;
        for (
            uint256 _principleIndex = 0;
            _principleIndex < _principleLength;
            _principleIndex++
        ) {
            if (s.nextEpochTimestamp[_principleIndex] <= block.timestamp) {
                s.nextEpochTimestamp[_principleIndex] = s
                    .nextEpochTimestamp[_principleIndex]
                    .add(s.epochLength); // set next epoch timestamp

                // distribute rewards to each recipient
                for (uint256 i = 0; i < s.info.length; i++) {
                    if (s.info[i].rate > 0) {
                        ITreasury(s.treasury).mintRewards( // mint and send from treasury
                            s.info[i].recipient,
                            nextRewardAt(s.info[i].rate)
                        );
                        adjust(i); // check for adjustment
                    }
                }
            } else {
                _success = false;
            }
        }

        return _success;
    }

    /* ====== INTERNAL FUNCTIONS ====== */

    /**
        @notice increment reward rate for collector
     */
    function adjust(uint256 _index) internal {
        LibBondStorage.DistributorAdjustment storage distributorAdjustment = s
            .distributorAdjustments[_index];

        if (distributorAdjustment.rate != 0) {
            if (distributorAdjustment.add) {
                // if rate should increase
                s.info[_index].rate = s.info[_index].rate.add(
                    distributorAdjustment.rate
                ); // raise rate
                if (s.info[_index].rate >= distributorAdjustment.target) {
                    // if target met
                    s.distributorAdjustments[_index].rate = 0; // turn off distributorAdjustment
                }
            } else {
                // if rate should decrease
                s.info[_index].rate = s.info[_index].rate.sub(
                    distributorAdjustment.rate
                ); // lower rate
                if (s.info[_index].rate <= distributorAdjustment.target) {
                    // if target met
                    s.distributorAdjustments[_index].rate = 0; // turn off adjustment
                }
            }
        }
    }

    /* ====== VIEW FUNCTIONS ====== */

    /**
        @notice view function for next reward at given rate
        @param _rate uint
        @return uint
     */
    function nextRewardAt(uint256 _rate) public view returns (uint256) {
        return IERC20(s.Necc).totalSupply().mul(_rate).div(1000000);
    }

    /**
        @notice view function for next reward for specified address
        @param _recipient address
        @return uint
     */
    function nextRewardFor(address _recipient) public view returns (uint256) {
        uint256 reward;
        for (uint256 i = 0; i < s.info.length; i++) {
            if (s.info[i].recipient == _recipient) {
                reward = nextRewardAt(s.info[i].rate);
            }
        }
        return reward;
    }
}

contracts/lib/LibsNeccStorage.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./LibDiamond.sol";

library LibsNeccStorage {
    using SafeMath for uint256;

    struct Layout {
        address nNecc; // staked necc
        address stakingContract; // staked necc
    }

    bytes32 internal constant STORAGE_SLOT =
        keccak256("necc.dao.sNecc.storage");

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }

    function _onlyStakingContract(Layout storage n) internal view {
        require(msg.sender == n.stakingContract, "NDOL: only stakingContract");
    }

    function _onlyGov() internal view {
        LibDiamond.enforceIsContractOwner();
    }

    function _gov() internal view returns (address) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        return ds.contractOwner;
    }

    function _setGov(address _newGov) internal {
        LibDiamond.enforceIsContractOwner();
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        ds.contractOwner = _newGov;
    }
}

