    function onlyGov() internal view {
        LibDiamond.enforceIsContractOwner();
    }
}

contracts/facets/Bond/StakingFacet.sol.content:
// SPDX-License-Identifier: AGPL-3.0-or-later
pragma abicoder v2;
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../../lib/LibBondStorage.sol";
import "./Facet.sol";

interface IDistributor {
    function distribute() external returns (uint256);
}

interface InNecc is IERC20 {
    function mint(address _to, uint256 _amount) external;

    function burn(address _from, uint256 _amount) external;

    function index() external view returns (uint256);

    function balanceFrom(uint256 _amount) external view returns (uint256);

    function balanceTo(uint256 _amount) external view returns (uint256);

    function migrate(address _staking, address _sOHM) external;
}

interface IsNecc is IERC20 {
    function rebase(uint256 neccProfit_, uint256 epoch_)
        external
        returns (uint256);

    function circulatingSupply() external view returns (uint256);

    function balanceOf(address who) external view override returns (uint256);

    function gonsForBalance(uint256 amount) external view returns (uint256);

    function balanceForGons(uint256 gons) external view returns (uint256);

    function index() external view returns (uint256);
}

contract StakingFacet is Facet {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using SafeERC20 for IsNecc;

    /**
        @notice stake Necc to enter warmup
        @param _amount uint
        @return bool
     */
    function stake(uint256 _amount, address _recipient)
        external
        returns (bool)
    {
        rebase();

        IERC20(s.Necc).safeTransferFrom(msg.sender, address(this), _amount);

        if (s.warmupPeriod == 0) {
            _send(_recipient, _amount);
        } else {
            LibBondStorage.Claim memory info = s.warmupInfo[_recipient];
            require(!info.lock, "Deposits for account are locked");

            s.warmupInfo[_recipient] = LibBondStorage.Claim({
                deposit: info.deposit.add(_amount),
                gons: info.gons.add(IsNecc(s.sNecc).gonsForBalance(_amount)),
                expiry: s.epoch.number.add(s.warmupPeriod),
                lock: false
            });

            s.gonsInWarmup = s.gonsInWarmup.add(
                IsNecc(s.sNecc).gonsForBalance(_amount)
            );
        }
        return true;
    }

    /**
        @notice retrieve sNecc from warmup
        @param _recipient address
     */
    function claim(address _recipient) public {
        LibBondStorage.Claim memory info = s.warmupInfo[_recipient];
        if (!info.lock) {
            require(
                _recipient == msg.sender,
                "External claims for account are locked"
            );
        }

        if (s.epoch.number >= info.expiry && info.expiry != 0) {
            delete s.warmupInfo[_recipient];
            s.gonsInWarmup = s.gonsInWarmup.sub(info.gons);

            _send(_recipient, IsNecc(s.sNecc).balanceForGons(info.gons));
        }
    }

    /**
        @notice forfeit sNecc in warmup and retrieve Necc
     */
    function forfeit() external {
        LibBondStorage.Claim memory info = s.warmupInfo[msg.sender];
        delete s.warmupInfo[msg.sender];
        s.gonsInWarmup = s.gonsInWarmup.sub(info.gons);

        IERC20(s.Necc).safeTransfer(msg.sender, info.deposit);
    }

    /**
        @notice redeem sNecc for Necc
        @param _amount uint
        @param _trigger bool
     */
    function unstake(uint256 _amount, bool _trigger) external {
        if (_trigger) {
            rebase();
        }

        InNecc(s.nNecc).burn(msg.sender, _amount); // amount was given in nNecc terms
        IERC20(s.Necc).safeTransfer(
            msg.sender,
            InNecc(s.nNecc).balanceFrom(_amount)
        );
    }

    /**
     * @notice send staker their amount as nNecc (non-rebasing)
     * @param _recipient address
     * @param _amount uint
     */
    function _send(address _recipient, uint256 _amount)
        internal
        returns (uint256)
    {
        InNecc(s.nNecc).mint(_recipient, InNecc(s.nNecc).balanceTo(_amount)); // send as nNecc (convert units from Necc)
        return InNecc(s.nNecc).balanceTo(_amount);
    }

    /**
        @notice returns the sNecc index, which tracks rebase growth
        @return uint
     */
    function index() public view returns (uint256) {
        return IsNecc(s.sNecc).index();
    }

    /**
        @notice trigger rebase if epoch over
     */
    function rebase() public {
        if (s.epoch.endTime <= uint256(block.timestamp)) {
            IsNecc(s.sNecc).rebase(s.epoch.distribute, s.epoch.number);

            s.epoch.endTime = s.epoch.endTime.add(s.epoch.length);
            s.epoch.number++;

            IDistributor(address(this)).distribute();

            if (contractBalance() <= totalStaked()) {
                s.epoch.distribute = 0;
            } else {
                s.epoch.distribute = contractBalance().sub(totalStaked());
            }
        }
    }

    /**
        @notice returns contract Necc holdings, including bonuses provided
        @return uint
     */
    function contractBalance() public view returns (uint256) {
        return IERC20(s.Necc).balanceOf(address(this)).add(s.totalBonus);
    }

    function epoch() public view returns (LibBondStorage.Epoch memory) {
        return s.epoch;
    }

    function warmupInfo(address _recipient)
        public
        view
        returns (LibBondStorage.Claim memory)
    {
        return s.warmupInfo[_recipient];
    }

    function supplyInWarmup() public view returns (uint256) {
        return IsNecc(s.sNecc).balanceForGons(s.gonsInWarmup);
    }

    function totalStaked() public view returns (uint256) {
        return IsNecc(s.sNecc).circulatingSupply();
    }
}

contracts/facets/Bond/DistributorFacet.s.content:
// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./Facet.sol";
import "../../lib/LibBondStorage.sol";
import "./BondDepositoryLib.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

interface ITreasury {
    function mintRewards(address _recipient, uint256 _amount) external;
}

contract DistributorFacet is Facet {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using BondDepositoryLib for LibBondStorage.Storage;

    /* ====== PUBLIC FUNCTIONS ====== */

    /**
        @notice send epoch reward to staking contract
     */
    function distribute() external returns (bool) {
        uint256 _principleLength = EnumerableSet.length(s.principles);
        bool _success = true;
        for (
            uint256 _principleIndex = 0;
            _principleIndex < _principleLength;
            _principleIndex++
        ) {
            if (s.nextEpochTimestamp[_principleIndex] <= block.timestamp) {
                s.nextEpochTimestamp[_principleIndex] = s
                    .nextEpochTimestamp[_principleIndex]
                    .add(s.epochLength); // set next epoch timestamp

                // distribute rewards to each recipient
                for (uint256 i = 0; i < s.info.length; i++) {
                    if (s.info[i].rate > 0) {
                        ITreasury(s.treasury).mintRewards( // mint and send from treasury
                            s.info[i].recipient,
                            nextRewardAt(s.info[i].rate)
                        );
                        adjust(i); // check for adjustment
                    }
                }
            } else {
                _success = false;
            }
        }

        return _success;
    }

    /* ====== INTERNAL FUNCTIONS ====== */

    /**
        @notice increment reward rate for collector
     */
    function adjust(uint256 _index) internal {
        LibBondStorage.DistributorAdjustment storage distributorAdjustment = s
            .distributorAdjustments[_index];

        if (distributorAdjustment.rate != 0) {
            if (distributorAdjustment.add) {
                // if rate should increase
                s.info[_index].rate = s.info[_index].rate.add(
                    distributorAdjustment.rate
                ); // raise rate
                if (s.info[_index].rate >= distributorAdjustment.target) {
                    // if target met
                    s.distributorAdjustments[_index].rate = 0; // turn off distributorAdjustment
                }
            } else {
                // if rate should decrease
                s.info[_index].rate = s.info[_index].rate.sub(
                    distributorAdjustment.rate
                ); // lower rate
                if (s.info[_index].rate <= distributorAdjustment.target) {
                    // if target met
                    s.distributorAdjustments[_index].rate = 0; // turn off adjustment
                }
            }
        }
    }

    /* ====== VIEW FUNCTIONS ====== */

    /**
        @notice view function for next reward at given rate
        @param _rate uint
        @return uint
     */
    function nextRewardAt(uint256 _rate) public view returns (uint256) {
        return IERC20(s.Necc).totalSupply().mul(_rate).div(1000000);
    }

    /**
        @notice view function for next reward for specified address
        @param _recipient address
        @return uint
     */
    function nextRewardFor(address _recipient) public view returns (uint256) {
        uint256 reward;
        for (uint256 i = 0; i < s.info.length; i++) {
            if (s.info[i].recipient == _recipient) {
                reward = nextRewardAt(s.info[i].rate);
            }
        }
        return reward;
    }
}

contracts/facets/Vault/VaultNdolFacet.so.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Facet.sol";
import "./VaultLib.sol";

// import "hardhat/console.sol";

interface INDOL {
    function mint(address _account, uint256 _amount) external;

    function burn(address _account, uint256 _amount) external;
}

contract VaultNdolFacet is Facet {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    event BuyNDOL(
        address account,
        address token,
        uint256 tokenAmount,
        uint256 ndolAmount
    );
    event SellNDOL(
        address account,
        address token,
        uint256 ndolAmount,
        uint256 tokenAmount
    );
    event Swap(
        address account,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut
    );

    function buyNDOL(address _token, address _receiver)
        external
        returns (uint256)
    {
        contractEntered();
        require(s.isInitialized, "Vault: not initialized");
        VaultLib.isTokenWhitelisted(s, _token);
        uint256 _price = VaultLib.getMinPrice(_token, s.includeAmmPrice);

        uint256 _tokenAmount = VaultLib.transferIn(s, _token);
        require(_tokenAmount > 0, "Vault: invalid tokenAmount");

        VaultLib.updateCumulativeFundingRate(s, _token);

        (, uint256 _amountAfterFees) = VaultLib._collectSwapFees(
            s,
            _token,
            _tokenAmount,
            true
        );

        uint256 _ndolAmount = VaultLib.adjustForDecimals(
            s,
            _amountAfterFees.mul(_price).div(PRICE_PRECISION),
            _token,
            s.ndol
        );

        require(_ndolAmount > 0, "Vault: invalid ndolAmount");

        VaultLib._increasePoolAmount(s, _token, _amountAfterFees);
        VaultLib._increaseNDOLAmount(s, _token, _ndolAmount);

        emit BuyNDOL(_receiver, _token, _tokenAmount, _ndolAmount);

        INDOL(s.ndol).mint(_receiver, _ndolAmount);

        contractExited();

        return _ndolAmount;
    }

    function sellNDOL(address _token, address _receiver)
        external
        returns (uint256)
    {
        contractEntered();
        VaultLib.isTokenWhitelisted(s, _token);

        uint256 _ndolAmount = VaultLib.transferIn(s, s.ndol);
        require(_ndolAmount > 0, "Vault: invalid ndolAmount");

        VaultLib.updateCumulativeFundingRate(s, _token);

        uint256 _redemptionAmount = getRedemptionAmount(_token, _ndolAmount);
        require(_redemptionAmount > 0, "Vault: invalid redemptionAmount");

        VaultLib._decreasePoolAmount(s, _token, _redemptionAmount);
        VaultLib._decreaseNDOLAmount(s, _token, _ndolAmount);

        INDOL(s.ndol).burn(address(this), _ndolAmount);

        // the _transferIn call increased the value of tokenBalances[ndol]
        // usually decreases in token balances are synced by calling _transferOut
        // however, for ndol, the tokens are burnt, so updateTokenBalance should
        // be manually called to record the decrease in tokens
        VaultLib.updateTokenBalance(s, s.ndol);

        (, uint256 _tokenAmount) = VaultLib._collectSwapFees(
            s,
            _token,
            _redemptionAmount,
            false
        );
        require(_tokenAmount > 0, "Vault: invalid tokenAmount");

        emit SellNDOL(_receiver, _token, _ndolAmount, _tokenAmount);

        VaultLib.transferOut(s, _token, _tokenAmount, _receiver);

        contractExited();

        return _tokenAmount;
    }

    function swap(
        address _tokenIn,
        address _tokenOut,
        address _receiver
    ) external returns (uint256) {
        contractEntered();
        require(s.isInitialized, "Vault: not initialized");
        require(_tokenIn != _tokenOut, "Vault: invalid tokens");
        VaultLib.isTokenWhitelisted(s, _tokenIn);
        VaultLib.isTokenWhitelisted(s, _tokenOut);

        VaultLib.updateCumulativeFundingRate(s, _tokenIn);
        VaultLib.updateCumulativeFundingRate(s, _tokenOut);

        uint256 amountIn = VaultLib.transferIn(s, _tokenIn);
        require(amountIn > 0, "Vault: invalid amountIn");

        uint256 priceIn = VaultLib.getMinPrice(_tokenIn, s.includeAmmPrice);
        uint256 priceOut = VaultLib.getMaxPrice(_tokenOut, s.includeAmmPrice);

        uint256 amountOut = amountIn.mul(priceIn).div(priceOut);
        amountOut = VaultLib.adjustForDecimals(
            s,
            amountOut,
            _tokenIn,
            _tokenOut
        );
        (, uint256 amountOutAfterFees) = VaultLib._collectSwapFees(
            s,
            _tokenOut,
            amountOut,
            false
        );

        // adjust ndolAmounts by the same ndolAmount as debt is shifted between the assets
        uint256 ndolAmount = amountIn.mul(priceIn).div(PRICE_PRECISION);
        ndolAmount = VaultLib.adjustForDecimals(
            s,
            ndolAmount,
            _tokenIn,
            s.ndol
        );

        VaultLib._increaseNDOLAmount(s, _tokenIn, ndolAmount);
        VaultLib._decreaseNDOLAmount(s, _tokenOut, ndolAmount);

        VaultLib._increasePoolAmount(s, _tokenIn, amountIn);
        VaultLib._decreasePoolAmount(s, _tokenOut, amountOut);

        emit Swap(_receiver, _tokenIn, _tokenOut, amountIn, amountOutAfterFees);

        VaultLib.transferOut(s, _tokenOut, amountOutAfterFees, _receiver);

        contractExited();

        return amountOutAfterFees;
    }

    function getRedemptionBasisPoints(address _token)
        public
        view
        returns (uint256)
    {
        return s.redemptionBasisPoints[_token];
    }

    function poolAmounts(address _token) public view returns (uint256) {
        return s.poolAmounts[_token];
    }

    function reservedAmounts(address _token) public view returns (uint256) {
        return s.reservedAmounts[_token];
    }

    function ndolAmounts(address _token) public view returns (uint256) {
        return s.ndolAmounts[_token];
    }

    function feeReserves(address _token) public view returns (uint256) {
        return s.feeReserves[_token];
    }

    function guaranteedUsd(address _token) public view returns (uint256) {
        return s.guaranteedUsd[_token];
    }

    function getRedemptionCollateral(address _token)
        public
        view
        returns (uint256)
    {
        uint256 _collateral = VaultLib.usdToTokenMin(
            s,
            _token,
            s.guaranteedUsd[_token]
        );
        return
            _collateral.add(s.poolAmounts[_token]).sub(
                s.reservedAmounts[_token]
            );
    }

    function getRedemptionCollateralUsd(address _token)
        public
        view
        returns (uint256)
    {
        return
            VaultLib.tokenToUsdMin(s, _token, getRedemptionCollateral(_token));
    }

    function availableReserve(address _token) public view returns (uint256) {
        uint256 _balance = IERC20(_token).balanceOf(address(this));
        return _balance.sub(s.reservedAmounts[_token]);
    }

    function getRedemptionAmount(address _token, uint256 _ndolAmount)
        public
        view
        returns (uint256)
    {
        uint256 _price = VaultLib.getMaxPrice(_token, s.includeAmmPrice);
        return
            VaultLib.adjustForDecimals(
                s,
                _ndolAmount.mul(PRICE_PRECISION).div(_price),
                s.ndol,
                _token
            );
    }

    function getTargetAdjustedFee(address _token, uint256 _fee)
        public
        view
        returns (uint256)
    {
        uint256 _initialAmount = s.ndolAmounts[_token];
        uint256 _targetAmount = VaultLib._getTargetNDOLAmount(s, _token);

        if (_targetAmount == 0 || _initialAmount == 0) {
            return _fee;
        } else if (_initialAmount > _targetAmount) {
            return _fee;
        }
        return _fee.mul(_initialAmount).div(_targetAmount);
    }

    function ndol() public view returns (address) {
        return s.ndol;
    }

    function swapFeeBasisPoints() public pure returns (uint256) {
        return SWAP_FEE_BASIS_POINTS;
    }
}

contracts/facets/Vault/Facet.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "../../lib/LibExchangeStorage.sol";
import "../../lib/LibDiamond.sol";

contract Facet {
    LibExchangeStorage.Storage internal s;

    constructor() {
        s.contractEntered = false;
    }

    function onlyGov() internal view {
        LibDiamond.enforceIsContractOwner();
    }

    function _setGov(address _newGov) internal {
        onlyGov();
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        ds.contractOwner = _newGov;
    }

    function contractEntered() internal {
        require(!s.contractEntered, "Contract already entered");
        s.contractEntered = true;
    }

    function contractExited() internal {
        s.contractEntered = false;
    }
}

contracts/facets/Vault/VaultLib.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IVaultPriceFeed.sol";
import "../../lib/LibExchangeStorage.sol";

// import "hardhat/console.sol";

library VaultLib {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    event IncreaseNDOLAmount(address token, uint256 amount);
    event DecreaseNDOLAmount(address token, uint256 amount);
    event CollectSwapFees(address token, uint256 feeUsd, uint256 feeTokens);
    event CollectMarginFees(address token, uint256 feeUsd, uint256 feeTokens);
    event IncreasePoolAmount(address token, uint256 amount);
    event DecreasePoolAmount(address token, uint256 amount);
    event UpdateFundingRate(address token, uint256 fundingRate);
    event UpdatePnl(bytes32 key, bool hasProfit, uint256 delta);
    event IncreaseReservedAmount(address token, uint256 amount);
    event DecreaseReservedAmount(address token, uint256 amount);
    event IncreaseGuaranteedUsd(address token, uint256 amount);
    event DecreaseGuaranteedUsd(address token, uint256 amount);

    function _getNextFundingRate(
        LibExchangeStorage.Storage storage s,
        address _token
    ) internal view returns (uint256) {
        if (
            s.lastFundingTimes[_token].add(FUNDING_INTERVAL) > block.timestamp
        ) {
            return 0;
        }

        uint256 _intervals = block
            .timestamp
            .sub(s.lastFundingTimes[_token])
            .div(FUNDING_INTERVAL);
        uint256 _poolAmount = s.poolAmounts[_token];
        if (_poolAmount == 0) {
            return 0;
        }

        return
            FUNDING_RATE_FACTOR
                .mul(s.reservedAmounts[_token])
                .mul(_intervals)
                .div(_poolAmount);
    }

    function updateCumulativeFundingRate(
        LibExchangeStorage.Storage storage s,
        address _token
    ) internal {
        if (s.lastFundingTimes[_token] == 0) {
            s.lastFundingTimes[_token] = block
                .timestamp
                .div(FUNDING_INTERVAL)
                .mul(FUNDING_INTERVAL);
            return;
        }

        if (
            s.lastFundingTimes[_token].add(FUNDING_INTERVAL) > block.timestamp
        ) {
            return;
        }

        uint256 fundingRate = _getNextFundingRate(s, _token);
        s.cumulativeFundingRates[_token] = s.cumulativeFundingRates[_token].add(
            fundingRate
        );
        s.lastFundingTimes[_token] = block.timestamp.div(FUNDING_INTERVAL).mul(
            FUNDING_INTERVAL
        );

        emit UpdateFundingRate(_token, s.cumulativeFundingRates[_token]);
    }

    function adjustForDecimals(
        LibExchangeStorage.Storage storage s,
        uint256 _amount,
        address _tokenDiv,
        address _tokenMul
    ) internal view returns (uint256) {
        uint256 _decimalsDiv = _tokenDiv == s.ndol
            ? NDOL_DECIMALS
            : s.tokenDecimals[_tokenDiv];
        uint256 _decimalsMul = _tokenMul == s.ndol
            ? NDOL_DECIMALS
            : s.tokenDecimals[_tokenMul];

        return _amount.mul(10**_decimalsMul).div(10**_decimalsDiv);
    }

    function getMinPrice(address _token, bool includeAmmPrice)
        internal
        view
        returns (uint256)
    {
        return
            IVaultPriceFeed(address(this)).getPrice(
                _token,
                false,
                includeAmmPrice
            );
    }

    function getMaxPrice(address _token, bool includeAmmPrice)
        internal
        view
        returns (uint256)
    {
        return
            IVaultPriceFeed(address(this)).getPrice(
                _token,
                true,
                includeAmmPrice
            );
    }

    function isTokenWhitelisted(
        LibExchangeStorage.Storage storage s,
        address _token
    ) internal view {
        require(s.whitelistedTokens[_token], "Vault: token not whitelisted");
    }

    function isPoolAmountBelowBalance(
        LibExchangeStorage.Storage storage s,
        address _token
    ) internal view {
        uint256 balance = IERC20(_token).balanceOf(address(this));
        require(s.poolAmounts[_token] <= balance, "Vault: invalid increase");
    }

    function transferIn(LibExchangeStorage.Storage storage s, address _token)
        internal
        returns (uint256)
    {
        uint256 prevBalance = s.tokenBalances[_token];
        uint256 nextBalance = IERC20(_token).balanceOf(address(this));
        s.tokenBalances[_token] = nextBalance;

        return nextBalance.sub(prevBalance);
    }

    function transferOut(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _amount,
        address _receiver
    ) internal {
        s.tokenBalances[_token] = IERC20(_token).balanceOf(address(this)).sub(
            _amount
        );

        IERC20(_token).safeTransfer(_receiver, _amount);
    }

    function tokenToUsdMin(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _tokenAmount
    ) internal view returns (uint256) {
        if (_tokenAmount == 0) {
            return 0;
        }
        uint256 _price = getMinPrice(_token, s.includeAmmPrice);
        uint256 _decimals = s.tokenDecimals[_token];

        return _tokenAmount.mul(_price).div(10**_decimals);
    }

    function usdToTokenMin(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _usdAmount
    ) internal view returns (uint256) {
        if (_usdAmount == 0) {
            return 0;
        }
        return
            usdToToken(
                s,
                _token,
                _usdAmount,
                getMaxPrice(_token, s.includeAmmPrice)
            );
    }

    function usdToToken(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _usdAmount,
        uint256 _price
    ) internal view returns (uint256) {
        if (_usdAmount == 0) {
            return 0;
        }
        uint256 _decimals = s.tokenDecimals[_token];

        return _usdAmount.mul(10**_decimals).div(_price);
    }

    function tokenToUsdMax(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _tokenAmount,
        bool includeAmmPrice
    ) internal view returns (uint256) {
        if (_tokenAmount == 0) {
            return 0;
        }
        uint256 _price = getMaxPrice(_token, includeAmmPrice);
        uint256 _decimals = s.tokenDecimals[_token];
        return _tokenAmount.mul(_price).div(10**_decimals);
    }

    function usdToTokenMax(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _usdAmount,
        bool includeAmmPrice
    ) internal view returns (uint256) {
        if (_usdAmount == 0) {
            return 0;
        }
        return
            usdToToken(
                s,
                _token,
                _usdAmount,
                getMinPrice(_token, includeAmmPrice)
            );
    }

    function updateTokenBalance(
        LibExchangeStorage.Storage storage s,
        address _token
    ) internal {
        uint256 _nextBalance = IERC20(_token).balanceOf(address(this));

        s.tokenBalances[_token] = _nextBalance;
    }

    function validatePosition(uint256 _size, uint256 _collateral)
        internal
        pure
    {
        if (_size == 0) {
            require(_collateral == 0, "Vault: collateral should be withdrawn");
            return;
        }
        require(
            _size >= _collateral,
            "Vault: _size must be more than _collateral"
        );
    }

    function _validateRouter(address _account) internal view {
        if (msg.sender == _account) {
            return;
        }
        if (msg.sender == address(this)) {
            return;
        }
        revert("Vault: invalid msg.sender");
    }

    function validateTokens(
        LibExchangeStorage.Storage storage s,
        address _collateralToken,
        address _indexToken
    ) internal view {
        require(_collateralToken == _indexToken, "Vault: mismatched tokens");
        isTokenWhitelisted(s, _collateralToken);
        isTokenWhitelisted(s, _indexToken);
    }

    function getPositionKey(
        address _account,
        address _collateralToken,
        address _indexToken,
        bool _isLong
    ) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    _account,
                    _collateralToken,
                    _indexToken,
                    _isLong
                )
            );
    }

    function _collectSwapFees(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _amount,
        bool _isBuyingNDOL
    ) internal returns (uint256, uint256) {
        uint256 _targetAdjustedFee = _getTargetAdjustedFee(
            s,
            _token,
            SWAP_FEE_BASIS_POINTS
        );
        uint256 _afterFeeAmount = _amount
            .mul(
                BASIS_POINTS_DIVISOR.sub(
                    _isBuyingNDOL ? _targetAdjustedFee : SWAP_FEE_BASIS_POINTS
                )
            )
            .div(BASIS_POINTS_DIVISOR);
        uint256 _feeAmount = _amount.sub(_afterFeeAmount);

        s.feeReserves[_token] = s.feeReserves[_token].add(_feeAmount);

        emit CollectSwapFees(
            _token,
            VaultLib.tokenToUsdMin(s, _token, _feeAmount),
            _feeAmount
        );
        return (_feeAmount, _afterFeeAmount);
    }

    function _increasePoolAmount(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _amount
    ) internal returns (uint256) {
        s.poolAmounts[_token] = s.poolAmounts[_token].add(_amount);
        isPoolAmountBelowBalance(s, _token);

        emit IncreasePoolAmount(_token, _amount);

        return s.poolAmounts[_token];
    }

    function _decreasePoolAmount(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _amount
    ) internal {
        s.poolAmounts[_token] = s.poolAmounts[_token].sub(
            _amount,
            "Vault: poolAmount exceeded"
        );
        isReservedAmountBelowPoolAmount(s, _token);
        isPoolAmountBelowBalance(s, _token);

        emit DecreasePoolAmount(_token, _amount);
    }

    function isReservedAmountBelowPoolAmount(
        LibExchangeStorage.Storage storage s,
        address _token
    ) internal view {
        require(
            s.reservedAmounts[_token] <= s.poolAmounts[_token],
            "Vault: reserve exceeds pool"
        );
    }

    function _transferRemainingCollateral(
        LibExchangeStorage.Storage storage s,
        address _collateralToken,
        address _receiver,
        uint256 _usdOut,
        uint256 _usdOutAfterFee
    ) internal returns (uint256) {
        if (_usdOut > 0) {
            VaultLib._decreasePoolAmount(
                s,
                _collateralToken,
                VaultLib.usdToTokenMin(s, _collateralToken, _usdOut)
            );
            uint256 _amountOutAfterFees = VaultLib.usdToTokenMin(
                s,
                _collateralToken,
                _usdOutAfterFee
            );
            VaultLib.transferOut(
                s,
                _collateralToken,
                _amountOutAfterFees,
                _receiver
            );

            return _amountOutAfterFees;
        } else {
            return 0;
        }
    }

    function _increaseNDOLAmount(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _amount
    ) internal {
        s.ndolAmounts[_token] = s.ndolAmounts[_token].add(_amount);

        emit IncreaseNDOLAmount(_token, _amount);
    }

    function _decreaseNDOLAmount(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _amount
    ) internal {
        uint256 _value = s.ndolAmounts[_token];
        // since NDOL can be minted using multiple assets
        // it is possible for the NDOL debt for a single asset to be less than zero
        // the NDOL debt is capped to zero for this case
        if (_value <= _amount) {
            s.ndolAmounts[_token] = 0;
            emit DecreaseNDOLAmount(_token, _value);
            return;
        }
        s.ndolAmounts[_token] = _value.sub(_amount);

        emit DecreaseNDOLAmount(_token, _amount);
    }

    function _reduceCollateral(
        LibExchangeStorage.Storage storage s,
        address _account,
        address _collateralToken,
        address _indexToken,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        bool _isLong
    ) internal returns (uint256, uint256) {
        LibExchangeStorage.Position storage position = s.positions[
            getPositionKey(_account, _collateralToken, _indexToken, _isLong)
        ];

        uint256 fee = _collectMarginFees(
            s,
            _collateralToken,
            _sizeDelta,
            position.size,
            position.entryFundingRate
        );
        bool hasProfit;
        uint256 adjustedDelta;

        // scope variables to avoid stack too deep errors
        {
            (bool _hasProfit, uint256 delta) = _getDelta(
                s,
                _indexToken,
                position.size,
                position.averagePrice,
                _isLong,
                position.lastIncreasedTime
            );
            hasProfit = _hasProfit;
            // get the proportional change in pnl
            adjustedDelta = _sizeDelta.mul(delta).div(position.size);
        }

        uint256 usdOut;
        // transfer profits out
        if (hasProfit && adjustedDelta > 0) {
            usdOut = adjustedDelta;
            position.realisedPnl = position.realisedPnl + int256(adjustedDelta);
        }

        if (!hasProfit && adjustedDelta > 0) {
            position.collateral = position.collateral.sub(adjustedDelta);
            position.realisedPnl = position.realisedPnl - int256(adjustedDelta);
        }

        // reduce the position's collateral by _collateralDelta
        // transfer _collateralDelta out
        if (_collateralDelta > 0) {
            usdOut = usdOut.add(_collateralDelta);
            position.collateral = position.collateral.sub(_collateralDelta);
        }

        // if the position will be closed, then transfer the remaining collateral out
        if (position.size == _sizeDelta) {
            usdOut = usdOut.add(position.collateral);
            position.collateral = 0;
        }

        // if the usdOut is more than the fee then deduct the fee from the usdOut directly
        // else deduct the fee from the position's collateral
        if (usdOut < fee) {
            position.collateral = position.collateral.sub(fee);
            uint256 feeTokens = VaultLib.usdToTokenMin(
                s,
                _collateralToken,
                fee
            );
            VaultLib._decreasePoolAmount(s, _collateralToken, feeTokens);
        }

        emit UpdatePnl(
            getPositionKey(_account, _collateralToken, _indexToken, _isLong),
            hasProfit,
            adjustedDelta
        );

        return (usdOut, usdOut > fee ? usdOut.sub(fee) : usdOut);
    }

    function _collectMarginFees(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _sizeDelta,
        uint256 _size,
        uint256 _entryFundingRate
    ) internal returns (uint256) {
        uint256 feeUsd = _getPositionFee(_sizeDelta);

        uint256 fundingFee = _getFundingFee(
            s,
            _token,
            _size,
            _entryFundingRate
        );
        feeUsd = feeUsd.add(fundingFee);

        uint256 feeTokens = VaultLib.usdToTokenMin(s, _token, feeUsd);
        s.feeReserves[_token] = s.feeReserves[_token].add(feeTokens);

        emit CollectMarginFees(_token, feeUsd, feeTokens);
        return feeUsd;
    }

    function _increaseReservedAmount(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _amount
    ) internal {
        s.reservedAmounts[_token] = s.reservedAmounts[_token].add(_amount);
        require(
            s.reservedAmounts[_token] <= s.poolAmounts[_token],
            "Vault: reserve exceeds pool"
        );

        emit IncreaseReservedAmount(_token, _amount);
    }

    function _decreaseReservedAmount(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _amount
    ) internal {
        s.reservedAmounts[_token] = s.reservedAmounts[_token].sub(
            _amount,
            "Vault: insufficient reserve"
        );

        emit DecreaseReservedAmount(_token, _amount);
    }

    function _increaseGuaranteedUsd(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _usdAmount
    ) internal {
        s.guaranteedUsd[_token] = s.guaranteedUsd[_token].add(_usdAmount);

        emit IncreaseGuaranteedUsd(_token, _usdAmount);
    }

    function _decreaseGuaranteedUsd(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _usdAmount
    ) internal {
        s.guaranteedUsd[_token] = s.guaranteedUsd[_token].sub(_usdAmount);

        emit DecreaseGuaranteedUsd(_token, _usdAmount);
    }

    function _getDelta(
        LibExchangeStorage.Storage storage s,
        address _indexToken,
        uint256 _size,
        uint256 _averagePrice,
        bool _isLong,
        uint256 _lastIncreasedTime
    ) internal view returns (bool, uint256) {
        require(_averagePrice > 0, "Vault: invalid _averagePrice");
        uint256 price = _isLong
            ? VaultLib.getMinPrice(_indexToken, s.includeAmmPrice)
            : VaultLib.getMaxPrice(_indexToken, s.includeAmmPrice);
        uint256 priceDelta = _averagePrice > price
            ? _averagePrice.sub(price)
            : price.sub(_averagePrice);
        uint256 delta = _size.mul(priceDelta).div(_averagePrice);

        bool hasProfit;

        if (_isLong) {
            hasProfit = price > _averagePrice;
        } else {
            hasProfit = _averagePrice > price;
        }

        // if the minProfitTime has passed then there will be no min profit threshold
        // the min profit threshold helps to prevent front-running issues
        uint256 minBps = block.timestamp >
            _lastIncreasedTime.add(MIN_PROFIT_TIME)
            ? 0
            : s.minProfitBasisPoints[_indexToken];
        if (hasProfit && delta.mul(BASIS_POINTS_DIVISOR) <= _size.mul(minBps)) {
            delta = 0;
        }

        return (hasProfit, delta);
    }

    function _getFundingFee(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _size,
        uint256 _entryFundingRate
    ) internal view returns (uint256) {
        if (_size == 0) {
            return 0;
        }

        uint256 fundingRate = s.cumulativeFundingRates[_token].sub(
            _entryFundingRate
        );
        if (fundingRate == 0) {
            return 0;
        }

        return _size.mul(fundingRate).div(FUNDING_RATE_PRECISION);
    }

    function _getPositionFee(uint256 _sizeDelta)
        internal
        pure
        returns (uint256)
    {
        if (_sizeDelta == 0) {
            return 0;
        }
        uint256 afterFeeUsd = _sizeDelta
            .mul(BASIS_POINTS_DIVISOR.sub(MARGIN_FEE_BASIS_POINTS))
            .div(BASIS_POINTS_DIVISOR);
        return _sizeDelta.sub(afterFeeUsd);
    }

    function _getTargetAdjustedFee(
        LibExchangeStorage.Storage storage s,
        address _token,
        uint256 _fee
    ) internal view returns (uint256) {
        uint256 _initialAmount = s.ndolAmounts[_token];
        uint256 _targetAmount = _getTargetNDOLAmount(s, _token);

        if (_targetAmount == 0 || _initialAmount == 0) {
            return _fee;
        } else if (_initialAmount > _targetAmount) {
            return _fee;
        }
        return _fee.mul(_initialAmount).div(_targetAmount);
    }

    function _getTargetNDOLAmount(
        LibExchangeStorage.Storage storage s,
        address _token
    ) internal view returns (uint256) {
        uint256 _ndolSupply = IERC20(s.ndol).totalSupply();
        if (_ndolSupply == 0) {
            return 0;
        }
        uint256 _tokenWeight = s.tokenWeights[_token];

        return _ndolSupply.mul(_tokenWeight).div(s.totalTokenWeight);
    }
}

contracts/lib/LibExchangeStorage.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

// import "hardhat/console.sol";
uint256 constant _NOT_ENTERED = 1;
uint256 constant _ENTERED = 2;

uint256 constant BASIS_POINTS_DIVISOR = 10000;
uint256 constant FUNDING_RATE_PRECISION = 10**6;
uint256 constant PRICE_PRECISION = 10**30;
uint256 constant NDOL_DECIMALS = 18;
uint256 constant ONE_USD = PRICE_PRECISION;
uint256 constant POSITION_PROPS_LENGTH = 9;

uint256 constant PRICE_SAMPLE_SPACE = 1;
uint256 constant MAX_LEVERAGE = 50 * 10000; // 50x
uint256 constant LIQUIDATION_FEE_USD = 5 * PRICE_PRECISION; // 5 USD
uint256 constant SWAP_FEE_BASIS_POINTS = 30; // 0.3%
uint256 constant MARGIN_FEE_BASIS_POINTS = 10; // 0.1%
uint256 constant MIN_PROFIT_TIME = 60 * 15; // 15 minutes
uint256 constant FUNDING_RATE_FACTOR = 600; // out of 1000
uint256 constant FUNDING_INTERVAL = 8 hours;

library LibExchangeStorage {
    bytes32 constant STORAGE_POSITION = keccak256("necc.dao.exchange.storage");

    struct Position {
        uint256 size;
        uint256 collateral;
        uint256 averagePrice;
        uint256 entryFundingRate;
        uint256 reserveAmount;
        int256 realisedPnl;
        uint256 lastIncreasedTime;
    }

    // Single global store
    struct Storage {
        bool contractEntered;
        address btc;
        address weth;
        address ndol;
        bool includeAmmPrice;
        bool isInitialized;
        EnumerableSet.AddressSet tokens;
        mapping(address => bool) whitelistedTokens;
        mapping(bytes32 => Position) positions;
        mapping(address => uint256) tokenDecimals;
        mapping(address => uint256) redemptionBasisPoints;
        mapping(address => uint256) minProfitBasisPoints;
        mapping(address => uint256) tokenBalances;
        mapping(address => uint256) ndolAmounts;
        mapping(address => uint256) poolAmounts;
        mapping(address => uint256) reservedAmounts;
        mapping(address => uint256) guaranteedUsd;
        mapping(address => uint256) cumulativeFundingRates;
        mapping(address => uint256) lastFundingTimes;
        mapping(address => uint256) feeReserves;
        mapping(address => uint256) tokenWeights;
        uint256 totalTokenWeight;
        // Router
        mapping(address => bool) plugins;
        // PriceFeed
        mapping(address => address) priceFeeds;
        mapping(address => uint256) priceDecimals;
        mapping(address => uint256) priceSpreadBasisPoints;
        mapping(address => address) baseTokenPairs; // x*y=k AMM Base Pair address i.e. wXUSDC
        mapping(address => address) tokenPairs; // x*y=k AMM Pair address i.e. wETHwX
        //
        int256 answer;
        uint80 roundId;
        mapping(uint80 => int256) answers;
        //
        uint256 totalNDOLMinted;
        mapping(address => mapping(address => int256)) ndolMinted; // [address][token] => amount
        // Always add new storage variable to the end of this struct
    }
}

