pragma solidity 0.7.5;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./Facet.sol";
import "./LibBondStorage.sol";
import "./BondDepositoryLib.sol";

interface IDepository {
    function setAdjustment(
        bool _addition,
        uint256 _delta,
        uint256 _seconds,
        address _principle
    ) external;

    function terms(address _principle)
        external
        view
        returns (LibBondStorage.Terms memory);
}

contract BCVPCFacet is Facet {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== MUTABLE FUNCTIONS ========== */

    // anyone can trigger a BCV update
    // if a bond is 20% underweight for the period, its BCV will increase 20% over next period
    function updateBCVs() external {
        uint256 _principlesLength = EnumerableSet.length(s.principles);

        for (
            uint256 _principleIndex = 0;
            _principleIndex < _principlesLength;
            _principleIndex++
        ) {
            address _principle = EnumerableSet.at(
                s.principles,
                _principleIndex
            );

            LibBondStorage.Terms memory _terms = IDepository(address(this))
                .terms(_principle);
            uint256 BCV = _terms.controlVariable;
            uint256 _principlePayoutSum = getPayoutSum(_principle);

            uint256 newBCV = _principlePayoutSum != 0 && s.targetSum != 0
                ? BCV.mul(_principlePayoutSum).div(s.targetSum)
                : BCV;

            if (s.weights[_principleIndex] != 0 && s.totalWeight != 0) {
                // higher weight means lower BCV meaning cheaper bond pricing
                // hence we do (total weight - principle weight) / total weight
                newBCV = newBCV
                    .mul(s.totalWeight.sub(s.weights[_principleIndex]))
                    .div(s.totalWeight);
            }

            if (newBCV > BCV) {
                IDepository(address(this)).setAdjustment(
                    true,
                    newBCV.sub(BCV),
                    s.epochLength * BCV_ADJUSTMENT_EPOCH_PERIOD,
                    _principle
                );
            } else {
                IDepository(address(this)).setAdjustment(
                    false,
                    BCV.sub(newBCV),
                    s.epochLength * BCV_ADJUSTMENT_EPOCH_PERIOD,
                    _principle
                );
            }
        }
    }

    /* ========== ONLY IDepository(address(this)) ========== */

    // IDepository(address(this)) stores the payout amount and block timestamp it occurred for each bond
    function storePayout(address _principle, uint256 _amount) public {
        require(msg.sender == address(this), "Only depository");
        uint256 _principleIndex = BondDepositoryLib.getIndexAt(s, _principle);

        s.payouts[_principleIndex].push(
            LibBondStorage.Payout({
                amount: _amount,
                blockTimestamp: block.timestamp
            })
        );
    }

    /* ========== ONLY POLICY ========== */

    // sets target sum of Necc paid per period length
    // (in 9 Necc decimals)
    function setTargetSum(uint256 _targetSum) external {
        onlyGov();
        s.targetSum = _targetSum;
    }

    // policy sets a weight for each bond (determines portion of target sum allocated)
    function setWeight(address _principle, uint256 _weight) external {
        onlyGov();
        uint256 _principleIndex = BondDepositoryLib.getIndexAt(s, _principle);
        s.totalWeight = s.totalWeight.add(_weight).sub(
            s.weights[_principleIndex]
        );
        s.weights[_principleIndex] = _weight;
    }

    /* ========== VIEW FUNCTIONS ========== */

    function targetSum() public view returns (uint256) {
        return s.targetSum;
    }

    // returns sum of Necc paid for a bond in the past period
    function getPayoutSum(address _principle)
        public
        view
        returns (uint256 sum_)
    {
        uint256 _principleIndex = BondDepositoryLib.getIndexAt(s, _principle);
        uint256 blockTimestampAfter = block.timestamp.sub(s.epochLength);

        LibBondStorage.Payout[] storage _principlePayouts = s.payouts[
            _principleIndex
        ];

        for (uint256 i = 0; i < _principlePayouts.length; i++) {
            if (_principlePayouts[i].blockTimestamp > blockTimestampAfter) {
                sum_ = sum_.add(_principlePayouts[i].amount);
            }
        }
    }
}
