pragma solidity 0.7.5;

import "../interfaces/INUSD.sol";
import "./YieldToken.sol";

contract NUSD is YieldToken, INUSD {
    mapping(address => bool) public vaults;

    modifier onlyVault() {
        require(vaults[msg.sender], "NUSD: forbidden");
        _;
    }

    constructor(address _vault) YieldToken("Necc USD", "NUSD", 0) {
        vaults[_vault] = true;
    }

    function addVault(address _vault) external onlyGov {
        vaults[_vault] = true;
    }

    function removeVault(address _vault) external onlyGov {
        vaults[_vault] = false;
    }

    function mint(address _account, uint256 _amount)
        external
        override
        onlyVault
    {
        _mint(_account, _amount);
    }

    function burn(address _account, uint256 _amount)
        external
        override
        onlyVault
    {
        _burn(_account, _amount);
    }
}
